        _;
    }

    constructor() ERC20("EGG", "EGG") {}

    function setStaff(address _user, bool _state) public onlyOwner {
        staff[_user] = _state;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function mintFromExtentionContract(
        address _user,
        uint256 _amount
    ) public onlyAllowed {
        _mint(_user, _amount);
    }

    function burnFromExtentionContract(
        address _user,
        uint256 _amount
    ) public onlyAllowed {
        _burn(_user, _amount);
    }
